import * as React from "react";
import './App.css'

class Component extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            Elevation: 0
        };
    }

    render(){
        let elevationCss = {
            border: this.state.Elevation
        };
        handleChange = (e) =>{this.setState({Elevation: e.target.value}, elevationCss = {border: this.state.Elevation}); console.log("Elevation "+e.target.value)}

        return(

            <div className="App">
                <header className="AppHeader"></header>
                <div style={elevationCss} className="textContainer">Here are some words</div>
                <input type="range" min="1" max="10" value={this.value} onChange={this.handleChange}></input>
                <button onClick={this.handleClick}>Change Border</button>
            </div>
        )
    }

}

export default Component
