import * as React from "react";
import './App.css'

class Component extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            width: 50
        };
    }


    render(){
        const style = {
            width: this.state.width

        }
        return(

            <div className="App">
                <header className="AppHeader"></header>
                <div style={style} className="textContainer">Here are some words</div>
                <input type="range" min="25" max="250" step = "25" value={this.value} onChange={this.handleChange}></input>
                <button onClick={this.handleClick}>Change Border</button>
            </div>
        )
    }
    handleChange = (e) => {this.setState({width: e.target.value }); console.log("Changed amount "+e.target.value)}
}

export default Component
