import * as React from "react";
import './App.css'

class Component extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            width: 50,
            height: 50,
            borderRadius: '0%'
        };
    }


    render(){
        const style = {
            width: this.state.width,
            height: this.state.height,
            borderRadius: this.state.borderRadius

        }
        console.log(style)
        return(

            <div className="App">
                <header className="AppHeader"></header>
                <div style={style} className="textContainer">Here are some words</div>
                <input type="range" min="25" max="250" step = "25" onChange={this.handleChange}></input>
                <div>
                    <button onClick={this.handleClick}>Change Height</button>
                </div>
                <div>Circle?
                    <label>
                        <input type="checkbox" onChange={this.handleCheck}></input>
                        <span className="slider"></span>
                    </label>
                </div>
            </div>
        )
    }
    handleChange = (e) => {this.setState({width: e.target.value }); console.log("Changed amount "+e.target.value)}
    handleClick = (e) =>{this.setState({height: Math.floor(Math.random()*100)+50});}
    handleCheck = (e) =>{console.log(e.target.value)}
}

export default Component
